create table goal_metrics (goals_goal_id integer not null, goal_metrics_id integer not null, goal_complete varchar(255), goal_in_progress varchar(255), money_made_on_goal varchar(255), time_until_due_date integer, primary key (goals_goal_id, goal_metrics_id)) engine=MyISAM
create table goals (users_user_id integer not null, goal_id integer not null, due_date datetime not null, description varchar(255), goal_complete varchar(255), goal_interval varchar(255), label_color varchar(255), pledge_amount double precision, task_amount integer, title varchar(255), primary key (users_user_id, goal_id, due_date)) engine=MyISAM
create table users (user_id integer not null, dob datetime, email varchar(255), gender varchar(255), password varchar(255), username varchar(255), primary key (user_id)) engine=MyISAM
